import { useEffect, useState } from "react";

const Sales = () => {
    
    const [soldProducts, setSoldProducts] = useState();


    function countRepeatedObjects(array) {
        // Create an empty object to store counts
        const counts = {};

        // Loop through the array
        array.forEach(obj => {
            // Create a key for the object based on its properties (excluding the "qty" property)
            const key = JSON.stringify(Object.fromEntries(Object.entries(obj).filter(([k]) => k !== 'qty')));

            // If the key already exists in the counts object, increment its count and update the "qty" property
            if (counts[key]) {
            counts[key].count++;
            counts[key].qty += obj.qty;
            } else {
            // If the key doesn't exist, initialize it with count 1 and "qty" equal to the object's "qty" property
            counts[key] = { ...obj, count: 1 };
            }
        });

        // Convert the counts object back to an array of objects
        const result = Object.values(counts);

        return result;
    }
    
    const sales = localStorage.getItem("sales");
    useEffect(() => {
        if (sales) {
            const salesData = JSON.parse(sales);
            const result = countRepeatedObjects(salesData);
            setSoldProducts(result);
        }
    })

    return (
        <div className="mt-11 mb-2 bg-gray-100 rounded-md p-3">
            {soldProducts && soldProducts.map(product => (
                <div className="bg-gray-200 mb-2 p-3 rounded-md">
                    <p>الإسم: span{product.name}</p>
                    <p>الكود: span{product.barcode}</p>
                    <p>السعر: span{product.price}</p>
                    <p>الكمية المباعة: span{product.qty}</p>
                    <p>عدد مرات البيع: span{product.count}</p>
                </div>
            ))}
        </div>
    );
}

export default Sales;
