import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { calculateTotalMoneyForSummary, getSalesSummary } from "../helpers";

const SalesReports = () => {

    const [sales, setSales] = useState();
    useEffect(() => {
        const salesData = localStorage.getItem("sales");
        if (salesData) {
            setSales(JSON.parse(salesData));
        }
    }, []);


    const createReportTextForSalesSummary = (sales) => {
        console.log("A");
        let text = `<i style="color:re>تقرير بملخص المبيعات** \n</i>**`;
        text += `
            اسم المنتج: ${sales.name}
            الكمية المُباعة: ${sales.totalSoldQty}
            عدد مرات البيع: ${sales.numOfRepeatSales}
            إجمالي المبلغ: ${sales.totalPrice}
        `
        text += `\n\n هذا التقرير تم إصداره في${new Date().toLocaleString("ar")}`;
        console.log(text);
    };
    const handleExportSalesSummeryReport = () => {
        if (sales) {
            const salesSummary = getSalesSummary(sales);
            const totalMoney = calculateTotalMoneyForSummary(sales);
            
            createReportTextForSalesSummary(salesSummary);

            console.log("object", salesSummary);
        } else {
            alert("لا توجد أي عملية بيع بعد!");
        }
    };
    const handleExportDetailedSalesReport = () => {};
    const handleExportDiscountedSalesReport = () => {};

    return (
        <div className="p-3 mb-2 bg-gray-100 rounded-md mt-11">
            <div className="flex justify-between">
                <button
                    className="p-2 rounded-md bg-gray-400 tex-white"
                    onClick={handleExportSalesSummeryReport}
                >
                    تصدير تقرير بمُلخص المبيعات
                </button>
                <button
                    className="p-2 rounded-md bg-gray-400 tex-white"
                    onClick={handleExportDetailedSalesReport}
                >
                    تصدير تقرير بتفاصيل عمليات البيع
                </button>
                <button
                    className="p-2 rounded-md bg-gray-400 tex-white"
                    onClick={handleExportDiscountedSalesReport}
                >
                    تصدير تقرير بالمبيعات المُخفّضة
                </button>
            </div>
            <div className="mt-5">
                <p>تم تصدير تقرير بمُلخص المبيعات</p>
                <Link
                    className="bg-green-500 text-white font-bold mt-2 rounded-md block text-center py-2"
                >
                    تحميل التقرير
                </Link>
            </div>
        </div>
    );
}

export default SalesReports;
