import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { calculateTotalMoneyForDetailedSales, calculateTotalMoneyForDiscountedSalesBeforeDiscount, calculateTotalMoneyForSummary, createReportTextForDiscountedSales, createReportTextForSalesHistory, createReportTextForSalesSummary, getDiscountedSales, getSalesSummary, getTotalSoldPiecesForSalesHistory, getTotalSoldPiecesForSummarySales } from "../helpers";
import Popup from "../components/Popup";

const SalesReports = () => {
    const DISCOUNT_PERCENTAGE = 0.10;

    const [reportText, setReportText] = useState();
    const [reportName, setReportName] = useState();

    // Load All Sales
    const [sales, setSales] = useState();
    useEffect(() => {
        const salesData = localStorage.getItem("sales");
        if (salesData) {
            setSales(JSON.parse(salesData));
        }
    }, []);

    const encodeText = (text) => {
        const encoder = new TextEncoder();
        return encoder.encode(text);
    }

    const downloadReport = () => {
        const blob = new Blob([encodeText(reportText)], { type: 'text/plain' });
        // Create The Download Link And Download The File
        const downloadLink = document.createElement("a");
        downloadLink.href = URL.createObjectURL(blob);
        downloadLink.download = `${reportName}.txt`;
        downloadLink.click();
        // Clean up the temporary URL after download (optional)
        URL.revokeObjectURL(downloadLink.href);
    };

    const handleExportSalesSummeryReport = () => {
        if (sales) {
            const salesSummary = getSalesSummary(sales);
            const totalMoney = calculateTotalMoneyForSummary(salesSummary);
            const totalSoldPieces = getTotalSoldPiecesForSummarySales(salesSummary); // Number Of All Sold Products
            const totalSoldItems = salesSummary.length; // Number Of The Different Sold Items

            const salesInfo = {
                sales: salesSummary,
                totalMoney: totalMoney,
                totalSoldItems: totalSoldItems,
                totalSoldPieces: totalSoldPieces,
            };
            setReportText(createReportTextForSalesSummary(salesInfo));
            setReportName("تقرير بملخص المبيعات");
        } else alert("لا توجد أي عملية بيع بعد!");
    };
    const handleExportDetailedSalesReport = () => {
        if (sales) {
            createDetailedSalesReport
            const totalMoney = calculateTotalMoneyForDetailedSales(sales);
            const totalSoldPieces = getTotalSoldPiecesForSalesHistory(sales);

            const salesInfo = {
                sales: sales,
                totalMoney: totalMoney,
                totalSoldPieces: totalSoldPieces,
            }
            setReportText(createReportTextForSalesHistory(salesInfo));
            setReportName("تقرير بسجل عمليات البيع");
        } else alert("لا توجد أي عملية بيع بعد!");
    };
    const handleExportDiscountedSalesReport = () => {
        if (sales) {
            const discountedSales = getDiscountedSales(sales);
            const totalMoney = calculateTotalMoneyForDiscountedSalesBeforeDiscount(discountedSales);
            const totalDiscountedSales = discountedSales.length;

            const salesInfo = {
                sales: discountedSales,
                totalMoney: totalMoney,
                totalDiscountsMoney: totalMoney * DISCOUNT_PERCENTAGE,
                totalDiscountsSales: totalDiscountedSales
            }
            setReportText(createReportTextForDiscountedSales(salesInfo));
            setReportName("تقرير بعمليات البيع المُخفضة");
        } else alert("لا توجد أي عملية بيع بعد!");
    };

    return (
        <div className="p-3 mb-2 bg-gray-100 rounded-md mt-11">
            <div className="flex justify-between">
                <button
                    className="p-2 rounded-md text-white bg-teal-400 hover:bg-teal-500 duration-300 tex-white"
                    onClick={handleExportSalesSummeryReport}
                >
                    تصدير تقرير بمُلخص المبيعات
                </button>
                <button
                    className="p-2 rounded-md text-white bg-teal-400 hover:bg-teal-500 duration-300  tex-white"
                    onClick={handleExportDetailedSalesReport}
                >
                    تصدير تقرير بتفاصيل عمليات البيع
                </button>
                <button
                    className="p-2 rounded-md text-white bg-teal-400 hover:bg-teal-500 duration-300 tex-white"
                    onClick={handleExportDiscountedSalesReport}
                >
                    تصدير تقرير بالمبيعات المُخفّضة
                </button>
            </div>
            {reportText && (
                <div className="mt-5">
                    <p>تم إصدار {reportName} بنجاح.</p>
                    <button
                        className="bg-green-500 hover:bg-green-600 duration-300 text-white font-bold mt-2 rounded-md w-full text-center py-2"
                        onClick={downloadReport}
                    >
                        تحميل التقرير
                    </button>
                </div>
            )}
        </div>
    );
}

export default SalesReports;
