import { useEffect, useState } from "react";
import Popup from "../components/Popup";
import { calculateTotalMoneyForSummary, countRepeatedSales } from "../helpers";

const SalesSummary = () => {
    const [soldProducts, setSoldProducts] = useState([]);
    const [totalMoney, setTotalMoney] = useState(0);
    // const [popupVisible, setPopupVisible] = useState(false);
    // const [productToDelete, setProductToDelete] = useState();

    // Set Initial Sold Products And Total Money
    const sales = localStorage.getItem("sales");
    useEffect(() => {
        if (sales) {
            let salesData = JSON.parse(sales);
            salesData = countRepeatedSales(salesData);
            setTotalMoney(calculateTotalMoneyForSummary(salesData));
            setSoldProducts(salesData);
        }
    },[]);

    // Handle Delete All Product Sales
    // const handleDeletion = (productBarcode) => { 
    //     setPopupVisible(true);
    //     setProductToDelete(productBarcode);
    // };
    // const deleteProduct = () => {
    //     // Update Sold Products
    //     const updatedSales = soldProducts.filter((product) => product.barcode !== productToDelete);
    //     // Update Local Sales State
    //     setSoldProducts(updatedSales);
    //     // Recalculate Total Money
    //     setTotalMoney(calculateTotalMoneyForSummary(updatedSales));
    //     // Update LocaleStorage
    //     localStorage.setItem("sales", JSON.stringify(updatedSales));
    //     // Close Modal and Reset State
    //     setPopupVisible(false);
    // };

    return (
       <>
          {/* Start Sales Section */}
          {soldProducts &&
             soldProducts.map((product) => (
                <div className="flex justify-between p-3 mb-2 bg-gray-200 rounded-md">
                   <div className="">
                      <p>
                         الإسم:{" "}
                         <span className="font-bold">{product.name}</span>
                      </p>
                      <p>
                         الكود:{" "}
                         <span className="font-bold">{product.barcode}</span>
                      </p>
                      <p>
                         الكمية المباعة:{" "}
                         <span className="font-bold">
                            {product.totalSoldQty}
                         </span>
                      </p>
                      <p>
                         عدد مرات البيع:{" "}
                         <span className="font-bold">
                            {product.numOfRepeatSales}
                         </span>
                      </p>
                      <p>
                         إجمالي المبلغ:{" "}
                         <span className="font-bold">
                            {product.totalPrice}$
                         </span>
                      </p>
                   </div>
                   <div className="">
                      {/* <button
                            className="block mb-3 transition duration-200 hover:bg-blue-300 hover:text-white"
                            >
                                Edit
                        </button> */}
                   </div>
                </div>
             ))}
          {/* End Sales Section */}

          {/* Start Total Money */}
          <div className="p-2 bg-yellow-400 rounded-md">
             إجمالي مبلغ المبيعات:
             <span className="font-bold ms-1">
                {/* {totalMoney.toLocaleString()}$ */}
                55
             </span>
          </div>
          {/* End Total Money */}
       </>
    );
}

export default SalesSummary;
