export const getProductByBarcode = async (barcode) => {
    try {
        const response = await fetch("db/products.json");
        if (!response.ok) {
            throw new Error('Failed to fetch data');
        }
        
        const data = await response.json();
        const product = data.find((product) => product.barcode === barcode);
        if (!product) {
            throw new Error('Product not found');
        }
        return product;
    } catch (error) {
        console.error('Error fetching data:', error);
        return null;
    }
};

export const addNewSell = async (product) => {
    try {
        // Get sales
        let sales = localStorage.getItem("sales");
        if (sales) {
            sales = JSON.parse(sales);
            sales.push(product);
            // Save product data to localStorage
            localStorage.setItem('sales', JSON.stringify(sales));
        } else {
            // Save product data to localStorage
            localStorage.setItem('sales', JSON.stringify([product]));
        }
        // Notify user about successful sale
        alert('Successful Sale!');
    } catch (error) {
        // Notify user about failed sale
        alert('Failed Sale!');
        console.error('Error while posting data:', error);
    }
};

export const setProductStockInDB = (barcode, newStock) => {
    awaigetProductByBarcode;
};
