import { useState } from "react";
import {getProductByBarcode} from "../helpers";
import BuyingProcess from "../components/BuyingProcess";


const Buy = () => {
    const [barcodeChecked, setBarcodeChecked] = useState(true);
    const [searchInputValue, setSearchInputValue] = useState();
    const [startBuyingProcess, setStartBuyingProcess] = useState(false);
    const [product, setProduct] = useState();
    const [error, setError] = useState();
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Reset States
        setProduct();
        setError();
        
        if (barcodeChecked) {
            const result = await getProductByBarcode(searchInputValue);
            if (result) setProduct(result);
            else setError("المنتج غير موجود!");
        }
    };

    return (
       <div className="mt-11 mb-2">
          {/* Start Search Section */}
          <form
             className="flex flex-row-reverse justify-center"
             onSubmit={handleSubmit}
          >
             <input
                type="text"
                className="border rounded-md w-80 ms-2 px-1 focus:outline-2 focus:outline-yellow-300"
                required
                value={searchInputValue}
                onChange={(e) => setSearchInputValue(e.target.value)}
             />
             <div className="flex gap-3">
                <label className="flex items-center select-none">
                   بالباركود
                   <input
                      type="radio"
                      checked={barcodeChecked}
                      onChange={() => setBarcodeChecked(!barcodeChecked)}
                   />
                </label>
                <label className="flex items-center select-none">
                   بالإسم
                   <input
                      type="radio"
                      checked={!isNameChecked}
                      onChange={() => setisNameChecked(!isNameChecked)}
                   />
                </label>
             </div>
             <button
                type="submit"
                className="bg-main-color px-3 py-2 font-bold rounded-md ml-5"
             >
                ابحث
             </button>
          </form>
          {/* End Search Section */}
          {/* Start Product Details */}
          <div className="bg-gray-100 mt-12 p-5">
             {!error && product && (
                <>
                   <p>الإسم: {product.name}</p>
                   <p className="my-1">
                      السعر:{" "}
                      <span className="font-bold">
                         {parseFloat(product.price).toFixed()}$
                      </span>
                   </p>
                   <p>المخزون: {product.stock}</p>
                   <button
                      className="bg-green-400 px-3 p-1 rounded-lg font-bold w-full my-5"
                      onClick={() => setStartBuyingProcess(!startBuyingProcess)}
                   >
                      بيع
                   </button>

                   {/* Start Buying Process */}
                   {startBuyingProcess && <BuyingProcess product={product} />}
                   {/* End Buying Process */}
                </>
             )}
             {!product && error && <p className="text-red-500">{error}</p>}
          </div>
          {/* End Product Details */}
       </div>
    );
}

export default Buy;
