import { useEffect, useState } from "react";
import Popup from "../components/Popup";
import { NavLink, Outlet } from "react-router-dom";

const SalesSummary = () => {
   const [soldProducts, setSoldProducts] = useState([]);
   const [totalMoney, setTotalMoney] = useState(0);
   const [popupVisible, setPopupVisible] = useState(false);

   // Set Sold Products And Total Money
   const sales = localStorage.getItem("sales");
   useEffect(() => {
      if (sales) {
         let salesData = JSON.parse(sales);
         salesData = countRepeatedSales(salesData);
         setSoldProducts(salesData);
         setTotalMoney(calculateTotalMoney(salesData));
      }
   }, []);

   return (
      <>
         <div className="p-3 mb-2 bg-gray-100 rounded-md mt-11">
            {/* Start Sales */}
            {soldProducts &&
               soldProducts.map((product) => (
                  <div className="flex justify-between p-3 mb-2 bg-gray-200 rounded-md">
                     <div className="">
                        <p>
                           الإسم:{" "}
                           <span className="font-bold">{product.name}</span>
                        </p>
                        <p>
                           الكود:{" "}
                           <span className="font-bold">{product.barcode}</span>
                        </p>
                        <p>
                           السعر:{" "}
                           <span className="font-bold">{product.price}</span>
                        </p>
                        <p>
                           الكمية المباعة:{" "}
                           <span className="font-bold">{product.qty}</span>
                        </p>
                        <p>
                           عدد مرات البيع:{" "}
                           <span className="font-bold">
                              {product.numOfRepeatSales}
                           </span>
                        </p>
                     </div>
                     <div className="">
                        <button className="block mb-3 transition duration-200 hover:bg-blue-300 hover:text-white">
                           Edit
                        </button>
                        <button
                           className="transition duration-200 hover:bg-red-600 hover:text-white"
                           onClick={() => handleDeletion(product.barcode)}
                        >
                           Del
                        </button>
                     </div>
                  </div>
               ))}
            {/* End Sales */}

            {/* Start Popup Container */}
            {popupVisible && (
               <Popup
                  text={`Are You Sure That You Want To Delete product with this barcode (${productToDelete}) ?`}
                  options={true}
                  confirmFun={deleteProduct}
                  closePopupFun={() => setPopupVisible(false)}
               />
            )}
            {/* End Popup Container */}

            {/* Start Total Money */}
            {/* End Total Money */}
            <div className="p-2 bg-yellow-400 rounded-md">
               إجمالي المبلغ:{" "}
               <span className="font-bold">{totalMoney.toLocaleString()}$</span>
            </div>
         </div>{" "}
         <div className="p-3 mb-2 bg-gray-100 rounded-md mt-11">
            <Outlet />
            {/* Start Sales */}
            {soldProducts &&
               soldProducts.map((product) => (
                  <div className="flex justify-between p-3 mb-2 bg-gray-200 rounded-md">
                     <div className="">
                        <p>
                           الإسم:{" "}
                           <span className="font-bold">{product.name}</span>
                        </p>
                        <p>
                           الكود:{" "}
                           <span className="font-bold">{product.barcode}</span>
                        </p>
                        <p>
                           السعر:{" "}
                           <span className="font-bold">{product.price}</span>
                        </p>
                        <p>
                           الكمية المباعة:{" "}
                           <span className="font-bold">{product.qty}</span>
                        </p>
                        <p>
                           عدد مرات البيع:{" "}
                           <span className="font-bold">
                              {product.numOfRepeatSales}
                           </span>
                        </p>
                     </div>
                     <div className="">
                        <button className="block mb-3 transition duration-200 hover:bg-blue-300 hover:text-white">
                           Edit
                        </button>
                        <button
                           className="transition duration-200 hover:bg-red-600 hover:text-white"
                           onClick={() => handleDeletion(product.barcode)}
                        >
                           Del
                        </button>
                     </div>
                  </div>
               ))}
            {/* End Sales */}

            {/* Start Popup Container */}
            {popupVisible && (
               <Popup
                  text={`Are You Sure That You Want To Delete product with this barcode (${productToDelete}) ?`}
                  options={true}
                  confirmFun={deleteProduct}
                  closePopupFun={() => setPopupVisible(false)}
               />
            )}
            {/* End Popup Container */}

            {/* Start Total Money */}
            {/* End Total Money */}
            <div className="p-2 bg-yellow-400 rounded-md">
               إجمالي المبلغ:{" "}
               <span className="font-bold">{totalMoney.toLocaleString()}$</span>
            </div>
         </div>
      </>
   );
};

