import { useState } from "react";
import {getProductByBarcode} from "../helpers";


const Buy = () => {
    const [barcodeChecked, setBarcodeChecked] = useState(true);
    const [searchInputValue, setSearchInputValue] = useState();
    const [product, setProduct] = useState();
    const [error, setError] = useState();
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (barcodeChecked) {
            const result = getProductByBarcode(searchInputValue);
            if (result) setProduct(result);
            else setError();
        }
    };

    return (
        <div className="mt-11 mb-2">
            {/* Start Search Section */}
            <form className="flex flex-row-reverse justify-center" onSubmit={handleSubmit}>
                <input
                    type="text"
                    className="border rounded-md w-80 ms-2 px-1 focus:outline-2 focus:outline-yellow-300" 
                    required
                    value={searchInputValue}
                    onChange={(e) => setSearchInputValue(e.target.value)}
                />
                <div className="flex gap-2">
                    <label className="flex items-center select-none">
                        بالباركود
                        <input type="radio" checked={barcodeChecked}  onChange={() => setBarcodeChecked(!barcodeChecked)} />
                    </label>
                    <label className="flex items-center select-none">
                        بالإسم
                        <input type="radio" checked={!barcodeChecked} onChange={() => setBarcodeChecked(!barcodeChecked)} />
                    </label>
                </div>
                <button 
                    type="submit" 
                    className="bg-main-color px-3 py-2 font-bold rounded-md ml-5"
                    >
                        ابحث
                </button>
            </form>
            {/* End Search Section */}
            {/* Start Product Details */}
            <div className="bg-gray-100 mt-12 p-5">
                {/* {product && ( */}
                <>
                <p>الإسم:</p>
                <p className="my-1">السعر: <span className="font-bold"></span></p>
                <p>الكمية: </p>
                <button className="bg-green-500 px-3 p-1 rounded-lg font-bold w-full my-5">بيع</button>

                {/* Start Buying Process */}
                {/* E Buying Process */}
                </>)
                {/* } */}
                {error && (<p className="text-red-500">ProductNot Found!</p>)}
            </div>
            {/* End Product Details */}
        </div>
    );
}

export default Buy;
