import { useState } from "react";
import { addNewSell } from "../helpers";

const BuyingProcess = ({ product }) => {
    const [qty, setQty] = useState(1);

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (product.stock >= qty) {
            await addNewSell({
                name: product.name,
                barcode: product.barcode,
                price: product.price,
                qty: qty
            });
        } else alert("enter valid quantity!")
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                الكمية:
                <input 
                    type="number" 
                    required
                    min={1}
                    max={product.stock}
                    value={qty} 
                    onChange={(e) => setQty(e.target.value)}
                    className="ms-3 w-10 text-center rounded border border-main-color"
                />
            </label>
            
            <label>
                الخصم:
                <input 
                    type="number" 
                    required
                    min={1}
                    max={product.stock}
                    value={qty} 
                    onChange={(e) => setQty(e.target.value)}
                    className="ms-3 w-10 text-center rounded border border-main-color"
                />
            </label>
            <button type="submit" className="block w-full bg-green-500 font-bold p-1 my-4 rounded-lg">إتمام الشراء</button>
        </form>
    );
}

export default BuyingProcess;
