import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { calculateTotalMoneyForSummary, getSalesSummary, getTotalSoldPiecesForSummarySales } from "../helpers";
import Popup from "../components/Popup";

const SalesReports = () => {
    const [popupVisible, setPopupVisible] = useState(false);
    const [popupInfo, setPopupInfo] = useState({});

    // Load All Sales
    const [sales, setSales] = useState();
    useEffect(() => {
        const salesData = localStorage.getItem("sales");
        if (salesData) {
            setSales(JSON.parse(salesData));
        }
    }, []);

    const encodeText = (text) => {
        const encoder = new TextEncoder();
        return encoder.encode(text);
    }

    const showPopup = (reportType) => {
        setPopupVisible(true);
        if (reportType === "sales summary") {
            setPopupInfo({
                text: "هل أنت متأكد من أنك تريد تحميل تقرير لم"
            });
            
        }
    }
    
    const createReportTextForSalesSummary = (salesInfo) => {
        const sales = salesInfo.sales;

        let text = `#تقرير بملخص المبيعات \n`;
        sales.map((sale) => {
            text += `
                اسم المنتج: ${sale.name}
                الكمية المُباعة: ${sale.totalSoldQty}
                عدد مرات البيع: ${sale.numOfRepeatSales}
                إجمالي المبلغ: $${sale.totalPrice}
            `
        });

        text += `\n إجمالي عدد الأصناف المُباعة: ${salesInfo.totalSoldItems}`;
        text += `\n إجمالي عدد القطع المُباعة: ${salesInfo.totalSoldPieces}`;
        text += `\n\n إجمالي مبلغ المبيعات: $${salesInfo.totalMoney.toLocaleString()}`;
        text += `\n هذا التقرير تم إصداره في ${new Date().toLocaleString("ar")}`;
        
        return text;
    };
    const downloadReport = (reportText, reportName) => {
        const blob = new Blob([encodeText(reportText)], { type: 'text/plain' });
        // Create The Download Link And Download The File
        const downloadLink = document.createElement("a");
        downloadLink.href = URL.createObjectURL(blob);
        downloadLink.download = `${reportName}.txt`;
        downloadLink.click();
        // Clean up the temporary URL after download (optional)
        URL.revokeObjectURL(downloadLink.href);
    };
    const handleExportSalesSummeryReport = () => {
        if (sales) {

            const salesSummary = getSalesSummary(sales);
            const totalMoney = calculateTotalMoneyForSummary(salesSummary);
            const totalSoldPieces = getTotalSoldPiecesForSummarySales(salesSummary); // Number Of All Sold Products
            const totalSoldItems = salesSummary.length; // Number Of The Different Sold Items

            const salesInfo = {
                sales: salesSummary,
                totalMoney: totalMoney,
                totalSoldItems: totalSoldItems,
                totalSoldPieces: totalSoldPieces
            }
            const reportText = createReportTextForSalesSummary(salesInfo);
            
            downloadReport(reportText, "تقرير بمُلخص المبيعات");
        } else alert("لا توجد أي عملية بيع بعد!");
    };
    const handleExportDetailedSalesReport = () => {};
    const handleExportDiscountedSalesReport = () => {};

    return (
        <div className="p-3 mb-2 bg-gray-100 rounded-md mt-11">
            <div className="flex justify-between">
                <button
                    className="p-2 rounded-md bg-gray-400 tex-white"
                    onClick={handleExportSalesSummeryReport}
                >
                    تصدير تقرير بمُلخص المبيعات
                </button>
                <button
                    className="p-2 rounded-md bg-gray-400 tex-white"
                    onClick={handleExportDetailedSalesReport}
                >
                    تصدير تقرير بتفاصيل عمليات البيع
                </button>
                <button
                    className="p-2 rounded-md bg-gray-400 tex-white"
                    onClick={handleExportDiscountedSalesReport}
                >
                    تصدير تقرير بالمبيعات المُخفّضة
                </button>
            </div>
            <div className="mt-5">
                <p>تم تصدير تقرير بمُلخص المبيعات</p>
                <Link
                    className="bg-green-500 text-white font-bold mt-2 rounded-md block text-center py-2"
                >
                    تحميل التقرير
                </Link>
            </div>

            {/* Start Popup Container */}
            {popupVisible &&
                <Popup
                    text={popupInfo.text}
                    options={popupInfo.options}
                    confirmFun={popupInfo.confirmFun}
                    closePopupFun={() => setPopupVisible(false)}
                />}
            {/* End Popup Container */}
        </div>
    );
}

export default SalesReports;
