export const getProducts = async () => {
    try {
        const response = await fetch("db/products.json");
        if (!response.ok) {
            throw new Error('Failed to fetch data');
        }
        
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('Error fetching data:', error);
        return null;
    }
};
export const getDiscountedSales = async () => {
    try {
        const response = await fetch("db/products.json");
        if (!response.ok) {
            
            throw new Error('Failed to fetch data');
        }
        
        const data = await response.json();
        console.log("object", data);
        const products = data.filter = (product) => product.discount === "نعم";
        if (!products) {
            throw new Error('Products not found');
        }
        return products;
    } catch (error) {
        console.error('Error fetching data:', error);
        return null;
    }
};
export const getProductByBarcode = async (barcode) => {
    try {
        const response = await fetch("db/products.json");
        if (!response.ok) {
            throw new Error('Failed to fetch data');
        }
        
        const data = await response.json();
        console.log(data);
        const product = data.find((product) => product.barcode === barcode);
        if (!product) {
            throw new Error('Product not found');
        }
        return product;
    } catch (error) {
        console.error('Error fetching data:', error);
        return null;
    }
};
export const searchOnProductByName = (products, targetProductName) => {
    const matchingProducts = []
    products.forEach((product) => {
        const productName = product.name;
        if (productName.toLowerCase().includes(targetProductName.toLowerCase())) {
            matchingProducts.push(product)
        }
    });
    return matchingProducts;
};
export const searchOnProductByBarcode = (products, barcode) => {
    const targetProduct = products.find(product => product.barcode === barcode);
    if (targetProduct) return targetProduct;
    return false;
};
export const addNewSell = async (product) => {
    try {
        // Get sales
        let sales = localStorage.getItem("sales");
        if (sales) {
            sales = JSON.parse(sales);
            sales.push(product);
            // Save product data to localStorage
            localStorage.setItem('sales', JSON.stringify(sales));
        } else {
            // Save product data to localStorage
            localStorage.setItem('sales', JSON.stringify([product]));
        }
        return true;
    } catch (error) {
        console.error('Error while posting data:', error);
        return false;
    }
};

export const countRepeatedSales = (salesData) => {
    // Create an empty object to store Sales Summary
    const repeatedSoldProducts = {};

    // Loop through the array
    salesData.forEach(soldProduct => {
        // Create a key for the object based on its properties (excluding the "qty" property)
        const key = JSON.stringify(Object.fromEntries(Object.entries(soldProduct).filter(([k]) => k !== 'qty')));

        // If the key already exists in the Sales Summary object, increment its count and update the "qty" property
        if (repeatedSoldProducts[key]) {
            repeatedSoldProducts[key].numOfRepeatSales++;
            repeatedSoldProducts[key].soldQTY = +repeatedSoldProducts[key].soldQTY + soldProduct.qty;
        } else {
            // If the key doesn't exist, initialize it with Num Of Repeat Sales 1 and "qty" equal to the object's "qty" property
            repeatedSoldProducts[key] = { ...soldProduct, numOfRepeatSales: 1 };
        }
    });

    // Convert the Sales Summary object back to an array of objects
    const result = Object.values(repeatedSoldProducts);
    return result;
}
export const calculateTotalMoneyForSummary = (totalSales) => {
    let total = 0;
    totalSales.forEach(sale => {
        console.log(sale.numberOfRepeatSales);
        total += sale.numOfRepeatSales * Math.ceil(sale.price);
    });
    return total;
};
export const calculateTotalMoneyForDetailedSales = (totalSales) => {
    let total = 0;
    totalSales.forEach(sale => {
        total += sale.qty * Math.ceil(sale.price);
    });
    return total;
};
