const Popup = ({text, options, confirmationFun, closePopupFun}) => {
    return (
    <div className="flex flex-col w-1/2 p-5 mx-auto text-center bg-white rounded-lg shadow-xl">
        <p className="mb-5 font-semibold">{text}</p>
        {options && (
            <button
            className="text-white bg-green-500 w-fit mx-auto px-2 py-1 rounded-lg font-bold hover:bg-green-600"
            onClick={() => closePopupFun()}
            >
            تم
            </button>
        )}
        {!options && (
            <div>
            <button
                className="text-white bg-green-500 w-fit mx-auto px-2 py-1 rounded-lg font-bold hover:bg-green-600"
                onClick={() => confirmationFun()}
            >
                حسناً
            </button>
            <button
                className="text-white bg-red-500 w-fit mx-auto px-2 py-1 rounded-lg font-bold hover:bg-green-600"
                onClick={(e) => closePopupFun()}
            >
                إلف
            </button>
            </div>
        )}
    </div>
    );
}

export default Popup;
