import { useEffect, useState } from "react";
import Popup from "../components/Popup";

const Sales = () => {
    const [soldProducts, setSoldProducts] = useState([]);
    const [totalMoney, setTotalMoney] = useState(0);
    const [popupVisible, setPopupVisible] = useState(false);
    const [productToDelete, setProductToDelete] = useState();


    function countRepeatedSales(salesData) {
        // Create an empty object to store Sales Summary
        const repeatedSoldProducts = {};

        // Loop through the array
        salesData.forEach(soldProduct => {
            // Create a key for the object based on its properties (excluding the "qty" property)
            const key = JSON.stringify(Object.fromEntries(Object.entries(soldProduct).filter(([k]) => k !== 'qty')));

            // If the key already exists in the Sales Summary object, increment its count and update the "qty" property
            if (repeatedSoldProducts[key]) {
                repeatedSoldProducts[key].numOfRepeatSales++;
                repeatedSoldProducts[key].soldQTY = +repeatedSoldProducts[key].soldQTY + soldProduct.qty;
            } else {
                // If the key doesn't exist, initialize it with Num Of Repeat Sales 1 and "qty" equal to the object's "qty" property
                repeatedSoldProducts[key] = { ...soldProduct, numOfRepeatSales: 1 };

            }
        });

        // Convert the Sales Summary object back to an array of objects
        const result = Object.values(repeatedSoldProducts);
        console.log("SSS ", result);

        return result;
    }
    
    const calculateTotalMoney = (totalSales) => {
        let total = 0;
        totalSales.forEach(sale => {
            console.log(sale.numberOfRepeatSales);
            total += sale.numOfRepeatSales * Math.cesale.price.to;
        });
        console.log(total, 's');
        return total;
    };

    const sales = localStorage.getItem("sales");
    useEffect(() => {
        if (sales) {
            let salesData = JSON.parse(sales);
            setSoldProducts(salesData);
            salesData = countRepeatedSales(salesData);
            setTotalMoney(calculateTotalMoney(salesData));
        }
    },[])

    const handleDeletion = (productBarcode) => {
        console.log("HI-1");
        setPopupVisible(true);
        setProductToDelete(productBarcode);
    };

    const deleteProduct = () => {
        // Update Sold Products
        const updatedSales = soldProducts.filter((product) => product.barcode !== productToDelete);
        // Update Local Sales State
        setSoldProducts(updatedSales);
        // Recalculate Total Money
        setTotalMoney(calculateTotalMoney(updatedSales));
        // Update LocaleStorage
        localStorage.setItem("sales", JSON.stringify(updatedSales));
        // Close Modal and Reset State
        setPopupVisible(false);
    };

    return (
        <div className="p-3 mb-2 bg-gray-100 rounded-md mt-11">
            {/* Start Sales */}
            {soldProducts && soldProducts.map(product => (
                <div className="flex justify-between p-3 mb-2 bg-gray-200 rounded-md">
                    <div className="">
                        <p>الإسم: <span className="font-bold">{product.name}</span></p>
                        <p>الكود: <span className="font-bold">{product.barcode}</span></p>
                        <p>السعر: <span className="font-bold">{product.price}</span></p>
                        <p>الكمية المباعة: <span className="font-bold">{product.qty}</span></p>
                        <p>عدد مرات البيع: <span className="font-bold">{product.numOfRepeatSales}</span></p>
                    </div>
                    <div className="">
                        <button
                            className="block mb-3 transition duration-200 hover:bg-blue-300 hover:text-white"
                            >
                                Edit
                        </button>
                        <button
                            className="transition duration-200 hover:bg-red-600 hover:text-white"
                            onClick={() => handleDeletion(product.barcode)}
                        >
                            Del
                        </button>
                    </div>
                </div>
            ))}
            {/* End Sales */}

            {/* Start Popup Container */}
            {popupVisible &&
                <Popup
                    text={`Are You Sure That You Want To Delete product with this barcode (${productToDelete}) ?`}
                    options={true}
                    confirmFun={deleteProduct}
                    closePopupFun={() => setPopupVisible(false)}
                />}
            {/* End Popup Container */}

            {/* Start Total Money */}
            {/* End Total Money */}
            <div className="p-2 bg-yellow-400 rounded-md">إجمالي المبلغ:  <span className="font-bold">{totalMoney.toLocaleString()}$</span></div>
        
        
        </div>
    );
}

export default Sales;
